function inject_unit_test_related_values(Array $option_def)
{
    $option_def['footer_spacing']['padding-top'] = '80px';
    $option_def['footer_spacing']['padding-bottom'] = '10px';

    return $option_def;
}

add_action('after_switch_theme', 'cleenday_get_default_option');
add_action('after_setup_theme', function() {
    $version = '1.0.2';
    if (get_option('cleenday_option_version') != $version) {
        cleenday_get_default_option();
        update_option('cleenday_option_version', $version);
    }
});

function cleenday_fonts_url()
{
    $fonts_url = '';

    /**
     * Translators: If there are characters in your language that are not supported
     * by Inter, translate this to 'off'. Do not translate into your own language.
     */
	$font1 = esc_html_x('on', 'Inter: on or off', 'cleenday');

    /**
     * Translators: If there are characters in your language that are not supported
     * by Spartan, translate this to 'off'. Do not translate into your own language.
     */
	$font2 = esc_html_x('on', 'Spartan: on or off', 'cleenday');

	if ('off' !== $font1 || 'off' !== $font2) {
		$font_families = [];

		if ('off' !== $font1) $font_families[] = 'Inter:400,500,600';
		if ('off' !== $font2) $font_families[] = 'Spartan:500,600,700';

		$query_args = [
			'family' => urlencode(implode('|', $font_families)),
			'subset' => urlencode('latin,latin-ext'),
		];

		$fonts_url = add_query_arg($query_args, 'https://fonts.googleapis.com/css');
	}

    return esc_url_raw($fonts_url);
}

add_action('admin_enqueue_scripts', function() {
    wp_enqueue_style('editor-google-fonts', cleenday_fonts_url(), [], '1.0.0');
});

if (!class_exists('Cleenday_CorePlugin')) {
    add_action('wp_enqueue_scripts', function () {
        wp_enqueue_style('cleenday-default-font', cleenday_fonts_url(), [], '1.0.0');
    });
}
